header img#menu-icon {
  max-width: 25px;
}

header h1 {
  display: inline-block;
  font-family: 'Dancing Script', sans-serif, Courier, monospace;
  font-weight: bold;
  margin: 5px 5px;
  font-size: calc(20px + 0.8vw);
  text-decoration: none;
