  width: 200px;
  margin-top: 35px;
}

main nav ul {
  padding-left: 0;
  width: 190px;
}

main nav h3 {
  margin-bottom: 2px;
  text-decoration: underline;
}

main nav ul li:hover {
  background-color: var(--dark-bg-color);
}

