  opacity: 1;
  pointer-events: auto;
}

main nav ul li {
  list-style-type: none;
  border: 1px solid black;
  padding: 5px;
  text-align: center;
  transition: all 0.2s ease-in-out;
  background-color: var(--slight-dark-bg-color);
