  font-weight: bold;
  margin: 25px 0px 10px;
}

div.category-list div.item div.title a:hover {
  font-size: 1.1rem;
}

div.item div.description img {
  max-width: 50%;
}

div.item div.description figure.cover {
  text-align: center;
}

