form legend {
  /* ESTILO DE LAS LEGENDAS QUE AGRUPAN ELEMENTOS DE UN FORMULARIO */
  font-size: 1.333rem; /* Tamaño referenciado al tamaño base de letra */
}

.note {
  color: gray;
  font-style: italic;
  font-size: 0.8889rem;
  margin-top: 30px;
}

form fieldset {
  width: 80%;
  margin: 0 auto;
  margin-bottom: 30px;
  background-color: var(--slight-dark-bg-color2);
  border-radius: 10px;
  border: 1px solid transparent;
