form textarea {
  height: 200px;
}

form fieldset p {
  display: flex;
  flex-direction: column;
}

form button {
  display: block;
  margin: 0 auto;
  background-color: var(--dark-bg-color);
  border: 1px solid var(--font-color);
  color: white;
  font-size: 1.222rem;
  border-radius: 5px;
  width: 200px;
  align-self: center;
